package codeWars.kyu6.Error_correction_1_hamming_code_20211025;

import static org.hamcrest.CoreMatchers.is;
import static org.hamcrest.MatcherAssert.assertThat;

import org.junit.jupiter.api.Test;

class CodewarsTest {

  @Test
  public void test_encode() {
    assertThat(new CodeWars().encode("hey"),
        is("000111111000111000000000000111111000000111000111000111111111111000000111"));
    assertThat(new CodeWars().encode("The Sensei told me that i can do this kata"),
        is(""));
    assertThat(new CodeWars().encode("T3st"),
        is("000111000111000111000000000000111111000000111111000111111111000000111111000111111111000111000000"));
  }

  @Test
  public void test_decode() {
    assertThat(new CodeWars()
            .decode("100111111000111001000010000111111000000111001111000111110110111000010111"),
        is("hey"));
  }
}